package datatypesandvariables;

public class ArithmaticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		
		//+ operator
		System.out.println(a+b);
		
		//- operator
		System.out.println(b-a);
		
		//* operator
		System.out.println(a*b);
		
		// / division
		System.out.println(b/a);
		
		//% mode
		System.out.println(17%19);
	}

}
